import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

class MyMenu2 extends React.Component {
  constructor() {
      this.addActiveClass= this.addActiveClass.bind(this);
      this.state = {
          active: false,
      };
  }
  toggleClass() {
      const currentState = this.state.active;
      this.setState({ active: !currentState });
  };

  render() {
      return (
        <div className={this.state.active ? 'topnav':'topnav responsive'}>
          <a href="#home" class="active">Home</a>
          <a href="#publications">Publications</a>
          <a href="#CV">CV</a>
          <a href="#" style={test} class="icon" onClick={this.toggleClass}>&#9776;</a>
        </div>
      )
  }
}

function MyMenu() {
  const [isActive, setActive] = useState(false);

  const toggleClass = () => {
    setActive(!isActive);
  };

  return (
    <div 
      className={isActive ? 'topnav responsive':'topnav'} 
    > <a href="#home" class="active">Home</a>
      <a href="#publications">Publications</a>
      <a href="#CV">CV</a>
      <a href="#" style={test} class="icon" onClick={toggleClass} >&#9776;</a>
    </div>
   );
}  

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
ReactDOM.render(
  <MyMenu />,
document.getElementById('mynav')
);

